let store = {
    _state : {
        currentFlag : 'sikvoa.png',
        ideologues : {
            communism : 0,
            monarchy : 66,
            imperialism : 33,
            democracy : 0,
        }
    },
    generateIdeologuesData(){
        let data = [];

        for (let ideology in this._state.ideologues){
            console.log(ideology)
            let population = this._state.ideologues[ideology];
            data.push({ideology : population});
        }
        return data;
    },
    get flag(){
        return `images/flags/${this._state.currentFlag}`;
    }
}
export default store;
