import CountiesState from "../counties-state";


let regionsStore = {

    update(){
        for (let region of this.regions){
            region.setColor();
        }
    },
    getValue(tag, type){
        let typeSumm = 0;
        for (let region of this.regions){
            if (region.owner.tag === tag){
                let typeValue = region[type];
                typeSumm += typeValue;
            }
        }
        return typeSumm;
    }
}
regionsStore.update();
export  default regionsStore;
