let store = {
    _state : {
        currentFlag : this.flags.democracy,
        flags : {
            democracy : 'democracy_sikvoa.png',
            monarchy  : 'sikvoa.png',
        },
        ideologues : {
            communism : 0,
            monarchy : 66,
            imperialism : 33,
            democracy : 0,
        },
        commanders : [
            {
                name : 'Глеб',
                image : 'images/commanders/gleb.png'
            },
            {
                name : 'Дима',
                image : 'images/commanders/dima.png'
            }
        ]
    },
    generateIdeologuesData(){
        let data = [];

        for (let ideology in this._state.ideologues){

            let population = this._state.ideologues[ideology];
            let dataObject = [ideology, population];
            data.push(dataObject)
        }
        return data;
    },
    get commanders(){
        return this._state.commanders;
    },
    get flag(){
        return `images/flags/${this._state.currentFlag}`;
    }
}
export default store;
