let commander = {
    name : '',
    image : '',
    influences(store){

    }
}
const IDEA_TAG = {
    MON : 'monarchy',
    DEM : 'democracy',
    IMP : 'imperialism',
    COM : 'communism',
}
let store = {
    _state : {
        flags : {
            democracy : 'democracy_sikvoa.png',
            monarchy  : 'sikvoa.png',
        },
        currentFlag : 'sikvoa.png',
        ideologues : {
            communism : 0,
            monarchy : 66,
            imperialism : 33,
            democracy : 0,
        },
        ideologuesDrift : {
            monarchy : 0,
            imperialism: 0,
            communism : 0,
            democracy : 0
        },
        commanders : [
            {
                __proto__ : commander,
                name : 'Глеб',
                image : 'images/commanders/gleb.png',
                influence(store){
                    store.setDrift(IDEA_TAG.IMP)
                }
            },
            {
                __proto__: commander,
                name : 'Дима',
                image : 'images/commanders/dima.png',
                influence(store) {
                    store.setDrift(IDEA_TAG.MON);
                }
            }
        ]
    },
    setDrift(ideology, driftValue){
        this._state.ideologuesDrift[ideology] += driftValue;
    },
    generateIdeologuesData(){
        let data = [];

        for (let ideology in this._state.ideologues){

            let population = this._state.ideologues[ideology];
            let dataObject = [ideology, population];
            data.push(dataObject)
        }
        return data;
    },
    get commanders(){
        return this._state.commanders;
    },
    get flag(){
        return `images/flags/${this._state.currentFlag}`;
    }
}
export default store;
