import {regions} from "./region-state";
let regionsStore = {
    update(){
        for (let region of regions){
            region.setColor();
        }
    },
    getValue(tag, type){
        let typeSumm = 0;
        for (let region of regions){
            if (region.owner.tag === tag){
                let typeValue = region[type];
                typeSumm += typeValue;
            }
        }
        return typeSumm;
    },
    setOwnerRegions(countriesState){
        for (let countryState of countriesState){
            for (let region of regions){
                if (region.ownerTag === countryState){
                    region.owner = countryState;
                }
            }
        }
    }
}
regionsStore.update();
export  default regionsStore;
