import React, {createRef} from "react";

class Region extends React.Component{
    constructor(props) {
        super(props);
        this.props = props;
        this.g = createRef();

    }
    componentDidMount() {

    }
    renderArmy(){

        if (this.g.current === null){
            return (
                <rect  x='100' y='100' height="10" width="10"   fill="#fff"/>
            )
        }
        return  (
            <rect fill={'black'} style={{zIndex:'1'}}  x={this.g.current.getBBox().x - this.g.current.getBBox().width / 2} y={this.g.current.getBBox().y + this.g.current.getBBox().height / 2}  height="10" width="20"/>
        )
    }
    render() {

        return(
            <g >


                {this.renderArmy()}
                <path ref={this.g} opacity={0.5} fill={this.props.data.color} d={this.props.data.d}/>
            </g>
        );
    }
}
export default Region;
