let commander = {
    name: '',
    image: '',
    options : '',
    influences(store) {

    },
    addOptions(options) {
        this.options += options + '\n';
    },
}
const IDEA_TAG = {
    MON : 'monarchy',
    DEM : 'democracy',
    IMP : 'imperialism',
    COM : 'communism',
}
let store = {
    _state : {
        flags : {
            democracy : 'democracy_sikvoa.png',
            monarchy  : 'sikvoa.png',
        },
        currentFlag : 'sikvoa.png',
        ideologues : {
            communism : 0,
            monarchy : 66,
            imperialism : 33,
            democracy : 0,
        },
        ideologuesDrift : {
            communism : 0,
            monarchy : 0,
            imperialism : 0,
            democracy : 0,
        },
        commanders : [
            {
                __proto__ : commander,
                name : 'Глеб',
                image : 'images/commanders/gleb.png',

                influence(store){
                    store.setDrift(IDEA_TAG.IMP, 1, this)
                },
            },
            {
                __proto__: commander,
                name : 'Дима',
                image : 'images/commanders/dima.png',

                influence(store) {
                    store.setDrift(IDEA_TAG.MON, 0.5, this);
                },


            }
        ]
    },
    normalizeIdeologues(){
        let summIdeology = 0;
      for (let ideology in this._state.ideologues){
          let ideologyValue = this._state.ideologues[ideology];
          summIdeology += ideologyValue;
      }
      for (let ideology in this._state.ideologues){
          this._state.ideologues[ideology] /= summIdeology;
      }
    },
    update(){
        this._state.ideologuesDrift = {
            communism : 0,
            monarchy : 0,
            imperialism : 0,
            democracy : 0,
        };
        for (let commander of this._state.commanders){
            commander.influence(this);
        }
        for (let ideology in this._state.ideologuesDrift){
            this._state.ideologues[ideology] += this._state.ideologuesDrift[ideology];
        }
        this.normalizeIdeologues();
    },

    setDrift(ideology, driftValue, user){
        this._state.ideologuesDrift[ideology] += driftValue;
        user.addOptions(`Поддержка идеологии ${ideology} +${driftValue}`);
    },
    generateIdeologuesData(){
        let data = [];

        for (let ideology in this._state.ideologues){

            let population = this._state.ideologues[ideology];
            let dataObject = [ideology, population];
            data.push(dataObject)
        }
        return data;
    },
    get commanders(){
        return this._state.commanders;
    },
    get flag(){
        return `images/flags/${this._state.currentFlag}`;
    }
}
console.log(store._state.commanders[0]);
store.update();
export default store;
